import React from 'react'

const Rating = ({text, value}) => {
    return (
        <div>
            <span>
                <i className={
                    value > 1  ? `fas fa-star`: value === 1.5 ? `far fa-star` : "fas fa-star-half-alt" 
                }></i>
                
                <i className={
                    value > 2  ? `fas fa-star`: value === 1.5 ? `far fa-star` : "fas fa-star-half-alt" 
                }></i>

                <i className={
                    value > 3  ? `fas fa-star`: value === 1.5 ? `far fa-star` : "fas fa-star-half-alt" 
                }></i>

                <i className={
                    value > 4  ? `fas fa-star`: value === 1.5 ? `far fa-star` : "fas fa-star-half-alt" 
                }></i>

                <i className={
                    value == 5  ? `fas fa-star`: value === 1.5 ? `far fa-star` : "fas fa-star-half-alt" 
                }></i>
            </span>
        </div>
    )
}

export default Rating
