import React from 'react'


let arr=[1,2,3,4,5];
const Rating = ({text, value}) => {
    return (
        arr.map(el => {
            for(let i = 1; i <= arr.length ; i++){
                value === i ? (
                    <i className='fas fa-star'></i>
                ) : value % 0.5 === 0 ? (
                    <i className='fas fa-star-half-alt'></i>
                ) : (
                    <i className='far fa-star'></i>
                )
            }
        })
    )
}

export default Rating
